targets.expr:
(
  100 * sum by (datacenter,cluster, svm) (
    volume_size_used{datacenter=~"$Datacenter", cluster=~"$Cluster", svm=~"$SVM"}
  )
  / on(datacenter, cluster, svm)
  sum by (datacenter,cluster, svm) (
    volume_size_total{datacenter=~"$Datacenter", cluster=~"$Cluster", svm=~"$SVM"}
  )
)
and on(datacenter,cluster, svm)
topk(
  $TopResources,
  (
    100 * sum by (datacenter,cluster, svm) (
      avg_over_time(volume_size_used{datacenter=~"$Datacenter", cluster=~"$Cluster", svm=~"$SVM"}[3h] @ end())
    )
    / on(datacenter,cluster, svm)
    sum by (datacenter,cluster, svm) (
      avg_over_time(volume_size_total{datacenter=~"$Datacenter", cluster=~"$Cluster", svm=~"$SVM"}[3h] @ end())
    )
  )
  * on(datacenter,cluster, svm) group_left
  svm_labels{datacenter=~"$Datacenter", cluster=~"$Cluster", svm=~"$SVM", root_svm=~"No|$IncludeRoot"}
)

