targets.expr:
sum by (datacenter, cluster, svm) (
    volume_size_used{datacenter=~"$Datacenter", cluster=~"$Cluster", svm=~"$SVM", style=~"flexvol|flexgroup"}
)
and on(datacenter, cluster, svm)
topk(
    $TopResources,
    sum by (datacenter, cluster, svm) (
        avg_over_time(volume_size_used{datacenter=~"$Datacenter", cluster=~"$Cluster", svm=~"$SVM", style=~"flexvol|flexgroup"}[3h])
    )
)
